import logo from './logo.svg';
import './App.css';
import { useState } from 'react';


function App() {
  const [tasks, setTasks] = useState([
    { title: 'Sample Task', completed: false }
  ]);

  const [inputValue, setInputValue] = useState('');

  const addTask = (taskTitle) => {
    const newTask = { title: taskTitle, completed: false };
    setTasks([...tasks, newTask]);
  };

  const toggleCompletion = (index) => {
    const updatedTasks = [...tasks];
    updatedTasks[index].completed = !updatedTasks[index].completed;
    setTasks(updatedTasks);
  };

  const deleteTask = (index) => {
    const updatedTasks = [...tasks];
    updatedTasks.splice(index, 1);
    setTasks(updatedTasks);
  };

  const [editingIndex, setEditingIndex] = useState(null);
  const startEditing = (index) => {
    setEditingIndex(index);
  };
  const saveEdit = (title) => {
    const updatedTasks = [...tasks];
    updatedTasks[editingIndex].title = title;
    setTasks(updatedTasks);
    setEditingIndex(null);
  };


  return (
    <div className="App">
      <h1>My To-Do List</h1>
      <input
        type="text"
        placeholder="Add a new task"
        value={inputValue}
        onChange={(e) => setInputValue(e.target.value)}
      />
      <button onClick={() => addTask(inputValue)}>Add</button> {/* Add task button */}
      <ul>
        {tasks.map((task, index) => (
          <li key={index}>
            {editingIndex === index ? (
              <>
                <input
                  type="text"
                  value={task.title}
                  onChange={(e) => {
                    const updatedTasks = [...tasks];
                    updatedTasks[index].title = e.target.value;
                    setTasks(updatedTasks);
                  }}
                />
                <button onClick={() => saveEdit(task.title)}>Save</button>
              </>
            ) : (
              <>
                {task.completed ? <s>{task.title}</s> : task.title}
                <button onClick={() => toggleCompletion(index)}>Toggle</button>
                <button onClick={() => deleteTask(index)}>Delete</button>
                <button onClick={() => startEditing(index)}>Edit</button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}
export default App;
